import { jsPDF } from "jspdf";
import autoTable from 'jspdf-autotable'
import { replace } from "./functionsJsPdf";

// Função para receber os dados para gerar o relatório
function externalDataPDF(data: any) {
  const _pdfPageOrientation = 'l' // 'l' = LANDSCAPE | 'p' = PORTRAIT
  const _pdfPageformat = 'a4'; // FORMATO A4
  let nomeArquivo = `Recibo#${data.Código_Recibo}`;

  let novoCabecalho = [
    "Alyanca Consultoria Contábil",
    "Silvana Aparecida Rodrigues Alves",
    "CRC 1SP 193 592/0-2",
    "Rua Eurides Fogaça, 55 - Trujilo - Cep 18.060-270 - Fone / Fax: (15) 3232-3476 / 3232-3571 (999122-9858)",
    "Sorocaba - Estado de São Paulo",
    "Abertura, Encerramento e Transferência de Firmas - Imposto de Renda",
    "e Administração de Condomínios",
    // `Recibo #${data.Código_Recibo}`,
    // `Emissão: ${data.Emissão}`,
    // `Razão Social: ${data.Razão_Social}`,
    // `Valor Total: ${data.ValorTotal}`
  ];

  const dataInfoHeader = [
    data.Código_Recibo,
    data.Emissão,
    data.Razão_Social
  ]

  const headers = [
    { content: 'Descrição' },
    { content: 'Competência' },
    { content: 'Valor' },
  ]

  data.CorpoRecibo.map((cr: any) => {
    delete cr.Código_Recibo;
    delete cr.CodDaDescrição;
  });

  let dataPDF = {
    config: {
      pdfName: nomeArquivo,
      pdfPageOrientation: _pdfPageOrientation,
      pdfPageformat: _pdfPageformat
    },
    header: novoCabecalho,
    table: {
      headers: {
        content: headers,
        // style: headStyle,
      },
      data: {
        infoHeader: dataInfoHeader,
        content: data.CorpoRecibo,
        footer: data.ValorTotal
        // style: bodyStyle,
      }
    }
  }
  return dataPDF;
}

// Função para criar o relatório com texto dinâmico e tabela agrupada
function configPDF(dataPDF: any) {

  // Cria uma instância do jsPDF
  const doc = new jsPDF({
    orientation: dataPDF.config.pdfPageOrientation,
    format: dataPDF.config.pdfPageformat,
  });

  let _yPosHeader = 15;
  const leftMargin = 20;
  const rightMargin = 20;
  const maxWidth = doc.internal.pageSize.width - leftMargin - rightMargin;

  // Função para adicionar textos com margens
  const addTexts = (textArrayHeader: any) => {
    textArrayHeader.forEach((item: any, index: number) => {
      let textLine = doc.splitTextToSize(item, maxWidth);

      const fontMap: any = {
        0: { size: 18, style: "bold" },
        1: { size: 10, style: "bold" },
        default: { size: 9, style: "normal" }
      };

      const { size, style } = fontMap[index] || fontMap.default;

      // Adiciona as linhas de texto ao documento
      textLine.forEach((txt: any) => {
        doc.setFontSize(size);
        doc.text(txt, 150, _yPosHeader, { align: 'center' }).setFont("", style);
        _yPosHeader += 5;
      });
      if (index === textArrayHeader.length - 1) {
        _yPosHeader -= 5;
      }
    });

    // Adiciona Razão Social
    let pos = _yPosHeader + 5
    doc.line(10, pos, doc.internal.pageSize.width - 10, pos)
    doc.line(10, pos + 10, doc.internal.pageSize.width - 10, pos + 10)

    const txt = `Nome:  ${dataPDF.table.data.infoHeader.at(2)}`;

    doc.setFontSize(14)

    let textLine = doc.splitTextToSize(txt, maxWidth)
    doc.text(textLine, 20, pos + 7);
  };

  const addTextDir = (dataInfo: any, pos: number) => {
    let posY = pos;
    // Adiciona texto lateral (Emissão e Nº)
    let txtInfo = [dataInfo];
    txtInfo.unshift(pos == 11 ? 'Recibo Nº' : 'Emissão');
    txtInfo.forEach((item: any, index: number) => {
      let textLine = doc.splitTextToSize(item, maxWidth);

      const fontMap: any = {
        0: { posX: doc.internal.pageSize.width - 55, size: 10 },
        1: { posX: doc.internal.pageSize.width - 40, size: 16 },
      };

      const { posX, size, style } = fontMap[index];

      // Adiciona as linhas de texto ao documento
      textLine.forEach((txt: any) => {
        doc.setFontSize(size);
        doc.text(txt, posX, posY, { align: 'center' });
        posY += 10;
      });
    });
  };

  const addTextFooter = () => {
    // Linha divosora
    doc.line(10, doc.internal.pageSize.height - 25, doc.internal.pageSize.width - 10, doc.internal.pageSize.height - 25)
    doc.setLineWidth(0.5);

    const txt1 = "Recebemos a(s) importância(s) acima discriminadas(s)";
    const txt2 = "Data Recebimento: ";
    const txt3 = "Assinatura:";

    // Adiciona as linhas de texto ao documento
    doc.setFontSize(11);
    doc.setFont("", "bold");
    let textLine = doc.splitTextToSize(txt1, maxWidth);
    doc.text(textLine, 20, doc.internal.pageSize.height - 20)
    let textLineData = doc.splitTextToSize(txt2, maxWidth);
    doc.text(textLineData, 20, doc.internal.pageSize.height - 10)
    let textLineAst = doc.splitTextToSize(txt3, maxWidth);
    doc.text(textLineAst, doc.internal.pageSize.width - 100, doc.internal.pageSize.height - 10)
    doc.line(doc.internal.pageSize.width - 75, doc.internal.pageSize.height - 10, doc.internal.pageSize.width - 15, doc.internal.pageSize.height - 10)
    doc.setLineWidth(0.5);
  };

  // Função para adicionar o cabeçalho
  const addHeader = () => {
    addImage();

    // Adiciona os textos com margens
    addTexts(dataPDF.header);

    // Linha divisora
    doc.line(doc.internal.pageSize.width - 66, 5, doc.internal.pageSize.width - 66, 50);
    doc.line(doc.internal.pageSize.width - 66, 28, doc.internal.pageSize.width - 10, 28)
    doc.setLineWidth(0.5);

    addTextDir(dataPDF.table.data.infoHeader.at(0), 11);
    addTextDir(dataPDF.table.data.infoHeader.at(1), 32);
  };

  // Estrutura dos cabeçalhos conforme a estrutura fornecida
  const transformHeaderStructure = (headers: any[]) => {
    let row1: any = [];
    let row2: any = [];
    let hasChildren = false;
    let rowSpan = headers.some(header => header.children && header.children.length > 0) ? 2 : 1;

    headers.forEach(header => {
      if (header.children && header.children.length > 0) {
        hasChildren = true;
        row1.push({ content: header.content, colSpan: header.children.length });
        header.children.forEach((child: any) => {
          row2.push({ content: child.content });
        });
      } else {
        row1.push({ content: header.content, rowSpan: rowSpan });
      }
    });

    return hasChildren ? [row1, row2] : [row1];
  }

  // Função para criar a tabela agrupada
  const addGroupedTable = (table: any, startY: any) => {
    const transformedHeaders = transformHeaderStructure(table.headers.content);
    const transformedBody = table.data.content.map((data: any) => [data.Descrição, data.Competência, data.ValorAPagar])
    transformedBody.push(['', 'TOTAL', table.data.footer])

    // Gerando a tabela do corpo
    autoTable(doc, {
      head: transformedHeaders,
      body: transformedBody,
      startY: startY + 15,
      theme: 'striped',
      styles: {
        cellWidth: 'wrap'
      },
      margin: {
        top: 5,
        horizontal: 10
      },
      headStyles: {
        fillColor: [75, 85, 99],
        textColor: [255, 255, 255],
        fontSize: 11,
        halign: 'center',
        valign: 'middle',
        cellPadding: 2,
        lineWidth: 0.5,
        lineColor: [192, 192, 192] //Silver
      },
      bodyStyles: {
        fontSize: 10,
        halign: 'right',
        valign: 'middle',
        cellPadding: 2,
        lineWidth: 0.5,
        textColor: [0, 0, 0],
        lineColor: [192, 192, 192] //Silver
      }
    });
  };

  // Adiciona imagem
  const addImage = () => {
    // Posição do topo do relatorio para a imagem
    let availablePageWidth = doc.internal.pageSize.width - 10;
    let imgWidth = 60;
    let imgHeight = 30;

    //Imagem convertida para base64
    var b64Image = replace(
      " ",
      "",
      "data:image/png;base64,",
      false
    );

    // Adiciona uma imagem no topo do relatório
    // addImage(imageData, format, x, y, width, height, alias, compression, rotation)
    doc.addImage(b64Image, "PNG", 10, 10, imgWidth, imgHeight, "img_logo", "NONE");
  };

  // Adicinar a borda
  doc.roundedRect(10, 5, doc.internal.pageSize.width - 20, doc.internal.pageSize.height - 10, 3, 3);

  // Adiciona o cabeçalho
  addHeader();

  // Adiciona a tabela abaixo dos textos dinâmicos
  addGroupedTable(dataPDF.table, _yPosHeader); // Adiciona um pequeno espaço entre o texto e a tabela
  addTextFooter();

  // Salva o PDF
  doc.save(`${dataPDF.config.pdfName}.pdf`);
};

export function generatePDF(dataPdf: any) {
  dataPdf.forEach((data: any) => {
    configPDF(externalDataPDF(data))
  });
}
